function Popup({ handleState, handleData, data }) {
  const closePopUp = () => {
    handleState(false);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newInfo = {
      title: event.target.subject.value,
      url: event.target.url.value,
      description: event.target.content.value,
    };
    handleData({ ...data, newInfo });
  };

  return (
    <form class="add-item-form">
      <button onClick={closePopUp}>X</button>
      <div class="question">
        <label>이름: </label>
        <input name="subject" required/>
      </div>
      <div class="question">
        <label>이미지 URL: </label>
        <input name="url" required />
      </div>
      <div class="question">
        <label>설명: </label>
        <input name="content" required />
      </div>
      <button onSubmit={handleSubmit}>저장</button>
    </form>
  );
}

export default Popup;
