import { useState } from 'react';
import Sidebar from './Sidebar';
import Card from './Card';
import Popup from './Popup';
import '../css/App.css';

function App() {
  const [isPopUpShow, setIsPopUpShow] = useState(false);
  const [drinkList, setDrinkList] = useState([{
    title: '소주',
    url: 'https://images.app.goo.gl/a55E1tkoGYFNxeqB7',
    description: '소주만 먹는건 별로입니다..',
  }]);

  return (
    <div className="App">
      <header>
        <span className="title">신나는 4주차 과제!</span>
      </header>
      <main>
        <Sidebar data={drinkList} handleState={setIsPopUpShow}/>
        <section className="container">
          {drinkList?.map(({ title, url, description }) => Card(title, url, description))}
        </section>
      </main>
      {isPopUpShow &&
        <Popup
          handleState={setIsPopUpShow}
          handleData={setDrinkList}
          data={drinkList}
        />
      }
    </div>
  );
}

export default App;
