function Popup({ handleState, handleData, data }) {
  const closePopUp = () => {
    handleState(false);
  };

  const handleSubmit = (event) => {
    console.log(event.target)
    const newInfo = {
      title: event.target.subject.value,
      url: event.target.url.value,
      description: event.target.content.value,
    };
    console.log(newInfo)
    handleData([...data, newInfo]);
  };

  return (
    <div className="modal-bg">
      <div className="modal">
        <form className="add-item-form" onSubmit={handleSubmit}>
          <span onClick={closePopUp}>X</span>
          <div className="question">
            <label>이름: </label>
            <input id="subject" name="subject" required/>
          </div>
          <div className="question">
            <label>이미지 URL: </label>
            <input id="image-url" name="url" required />
          </div>
          <div className="question">
            <label>설명: </label>
            <input id="content" name="content" required />
          </div>
          <button type="submit">저장</button>
        </form>
      </div>
    </div>
  );
}

export default Popup;
